package test;

public class While1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int no=12;
		System.out.println("While Loop");
		while(no<=11)
		{
			System.out.println(no);
			no++;
		}
		
		// Do While

		System.out.println("Do While Loop");
		int no1=12;
		do
		{
			System.out.println(no1);
			no1++;	
		}
		while(no1<=11);
	}

}
